import { useRef, useState } from 'react';
import styled from 'styled-components';

function Q1() {
    /* 
    문제1
    useState 및 styled-components에 관련한 문제입니다.
    아래 문제를 통해 어떠한 경우 state를 사용해야하는지 고민하여 풀어보세요
  
    1-1 )
        문제1-1은 input의 value가 placeholder의 값과 같아졌다면
        초록색 글씨로 올바르게 입력하셨습니다가 노출됩니다.

        만약 입력하지 않았다면 올바르게 글을 작성해주세요라는 문구가
        붉은색 글씨로 노출됩니다
        

    1-2 )
        문제1-2는 보이기 버튼을 누른다면

        button 내부의 텍스트는 숨기기로 바뀌고
        아래 보이는 p태그가 보여야합니다.

        반대로 숨기기 텍스트로 바뀐 button을 누르면
        p태그는 보이지 않아야합니다

  */
    const [input, setInput] = useState('');
    const textRef = useRef(null);
    const handleKeydown = (e) => {
        const { value } = e.target;
        setInput(value);
        handleChangeColor(e);
    };
    const handleChangeColor = (e) => {
        if (input == e.target.placeholder) {
            textRef.current.style.color = 'green';
            textRef.current.innerText = '올바르게 입력하였습니다.';
        } else if (input !== e.target.placeholder) {
            textRef.current.style.color = 'red';
            textRef.current.innerText = '올바르게 글을 작성해주세요';
        }
    };
    const [form, setForm] = useState(false);
    const pRef = useRef(null);

    const handleClickForm = (e) => {
        setForm((form) => !form);
        handleChangeText();
    };
    const handleChangeText = () => {
        if (form) {
            pRef.current.innerText = '보이기';
        } else {
            pRef.current.innerText = '숨기기';
        }
    };

    return (
        <>
            <h1>문제1</h1>
            <div>
                <h2>문제1-1.</h2>
                <input
                    type="text"
                    placeholder={'김성용'}
                    style={{ textAlign: 'center' }}
                    onKeyDown={handleKeydown}
                />
                <S.Message ref={textRef}> 올바르게 입력하셨습니다 </S.Message>
            </div>

            <div>
                <h2>문제1-2. </h2>
                <button ref={pRef} onClick={handleClickForm}>
                    보이기
                </button>
                {form && <p> 이 문구는 보이기 상태일 때만 볼 수 있습니다 </p>}
            </div>
        </>
    );
}
export default Q1;

const Message = styled.p``;

const S = {
    Message,
};
